using System.Collections; using System.Collections.Generic; using UnityEngine;  public class PlayerController : MonoBehaviour {     public float speed;      private Rigidbody rb;     private Transform transform;     //public float speed = 5;     //public float rotationSpeed = 5;     // Start is called before the first frame update     void Start()   {         rb = GetComponent<Rigidbody>();         //transform = GetComponent<Transform>();     }    // Update is called once per frame   void Update()   {     //float verticalRotation = Input.GetAxis("LookVertical");     //float horizontalRotation = Input.GetAxis("LookHorizontal");      //Vector3 rotationVector = new Vector3(verticalRotation, horizontalRotation, 0);      //transform.Rotate(rotationVector);   }    void FixedUpdate()   {         float moveHorizontal = Input.GetAxis("Horizontal");         float moveVertical = Input.GetAxis("Vertical");          Vector3 movement = new Vector3(moveHorizontal, 0.0f, moveVertical).normalized;          //rb.AddForce(movement * speed);         rb.velocity = movement * speed;          if (rb.velocity.magnitude < 0.01)         {             rb.velocity = Vector3.zero;             //rb.angularVelocity = Vector3.zero;         }     } }  